import {useSelector} from "react-redux";
import {useNavigate, useParams} from "react-router-dom";
import {useCallback, useEffect, useState} from "react";
import ImageUploader from "../../components/ImageUploader";
import api from "../../utils/api";
import TextArea from "../../components/TextArea";
import {Button} from "@mui/material";
import "../add-board/addBoard.scss";
import axios from "axios";
import {toast} from "react-toastify";
import "react-toastify/dist/ReactToastify.css";


const MarketChange = () => {
  const navigate = useNavigate();
  // URI ÌååÎùºÎØ∏ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
  const {board_id} = useParams();
  // Í≤åÏãúÌåê Ï†úÎ™©, ÎÇ¥Ïö©, ÏÇ¨ÏßÑ
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState({
    image_file: "",
    preview_URL: "img/default_image.png",
  });

  // ÏÇ¨Ïö©ÏûêÍ∞Ä ÏßÅÏ†ÑÏóê Îì±Î°ùÌïú Í≤åÏãúÎ¨ºÏùò ÏÉÅÌÉúÎ•º Í∑∏ÎåÄÎ°ú Î≥¥Ïó¨Ï£ºÍ∏∞ ÏúÑÌï¥
  // Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä ÎßàÏö¥Ìä∏ÎêòÍ≥† URI ÌååÎùºÎØ∏ÌÑ∞Ïóê Ìï¥ÎãπÌïòÎäî boardÎ•º Í∞ÄÏ†∏ÏôÄÏÑú
  // title, content, imageÏùò ÏÉÅÌÉúÎ•º Î∞îÍøîÏ§å
  useEffect(() => {
    const getBoard = async () => {
      const {data} = await axios.get(`/api/v1/boards/${boardsId}/detail`);
      return data;
    }
    getBoard().then((result) => {
      setTitle(result.title);
      setContent(result.content);
      // Ïù¥ÎØ∏ÏßÄÎäî ÌååÏùºÏùÑ Î∂àÎü¨Ïò¨ ÌïÑÏöîÍ∞Ä ÏóÜÏù¥ ÎØ∏Î¶¨Î≥¥Í∏∞ urlÎßå Í∞ÄÏ†∏Ïò®Îã§.
      // Ïù¥ÎØ∏ÏßÄÎ•º ÏÑ†ÌÉùÌïòÏßÄ ÏïäÍ≥† Ïò¨Î¶¨Î©¥ dbÏóê Ï†ÄÏû•ÎêòÏñ¥ ÏûàÎäî Ïù¥ÎØ∏ÏßÄÎ•º Í∑∏ÎåÄÎ°ú ÏÇ¨Ïö©!
      setImage(result.image)
    });
  }, [])

  const canSubmit = useCallback(() => {
    return content !== "" && title !== "";
  }, [image, title, content]);

  const handleSubmit = useCallback(async () => {
    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("content", content);
      // Ïù¥ÎØ∏ÏßÄÎ•º ÏÑ†ÌÉùÌñàÏùÑ ÎïåÎßå formdataÏóê ÎÑ£Ïùå
      formData.append("file", image.image_file);
      // ÏàòÏ†ïÌï† Îïê board_idÎ•º Î≥¥ÎÇ¥Ïûê
      formData.append("id", board_id);
      await api.put("/api/board", formData);
      window.alert("üòéÏàòÏ†ïÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§üòé");
      // Ïù¥Ï†Ñ ÌéòÏù¥ÏßÄÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
      window.location.href = `/marketdetail/${board_id}`;
    } catch (e) {
      // ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏùÄ ÏóêÎü¨ Î©îÏãúÏßÄ Ï∂úÎ†•
      toast.error("Ïò§Î•òÎ∞úÏÉù! Ïù¥Î™®ÏßÄÎ•º ÏÇ¨Ïö©ÌïòÎ©¥ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌï† Ïàò ÏûàÏäµÎãàÎã§" + "üò≠", {
        position: "top-center",
      });
    }

  }, [canSubmit]);

  return (
      <>
      <h1>ÏàòÏ†ïÌïòÍ∏∞</h1>
