import React, {useEffect, useState} from "react";
import axios from "axios";
import {useNavigate, useParams} from "react-router-dom";
import {Button, Dialog, DialogContent, IconButton} from "@mui/material";
import BuildOutlinedIcon from '@mui/icons-material/BuildOutlined';
import DeleteForeverOutlinedIcon from '@mui/icons-material/DeleteForeverOutlined';
import DisabledByDefaultOutlinedIcon from "@mui/icons-material/DisabledByDefaultOutlined";
const MarketDetail = () => {
  // URL ÌååÎùºÎØ∏ÌÑ∞ Î∞õÍ∏∞ - boardÏùò id
  const {boardId} = useParams();
  const [board, setBoard] = useState({});
  const [isLoaded, setIsLoaded] = useState(false);
  const token = localStorage.getItem("access");
  const id = localStorage.getItem("id");
  const navigate = useNavigate();
  // modalÏù¥ Î≥¥Ïù¥Îäî Ïó¨Î∂Ä ÏÉÅÌÉú
  const [show, setShow] = useState(false);
  // board Í∞ÄÏ†∏Ïò§Í∏∞

  // const id = localStorage.getItem("id");
  // const [data, setData] = useState([]);
  // const {boardsId} = useParams();

  // useEffect(() => {

  //     axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
  //     axios.get(`/api/v1/boards/${boardsId}/detail`)

  //     .then((response)  => { 
  //       setData(response.data);
  //       console.log(response)
  //     })

  //   }, [])


  useEffect(() => {
    const getBoard = async () => {
      const {data} =
      axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`
      await axios.get(`/api/board/${boardId}/detail`);
      return data;
    }
    getBoard().then(result => setBoard(result)).then(() => setIsLoaded(true));
  }, [])

    function Delete() {
      axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
      axios.delete(`/api/v1/boards/${this.boardsId}`)
      .then(
        window.location.href = "/market",
        alert('ÏÇ≠Ï†ú ÎêòÏóàÏäµÎãàÎã§.')
      )
  }

  return (
    <React.Fragment>
      {isLoaded && (
        <div className="board-wrapper">
          {
            /*
              Ìï¥Îãπ Í∏ÄÏùò ÏûëÏÑ±ÏûêÍ∞Ä Î°úÍ∑∏Ïù∏ÏùÑ ÌñàÏùÑ ÎïåÎßå ÏàòÏ†ï, ÏÇ≠Ï†ú Î≤ÑÌäºÏù¥ Î≥¥Ïù¥Í≤å ÌïòÏûê.
              Î°úÍ∑∏Ïù∏ÏùÑ Ìïú ÏÇ¨Ïö©ÏûêÏùò jwt-tokenÏóêÏÑú userÏùò IDÎ•º Ï∂îÏ∂úÌïú ÌõÑ,
              board(Ìï¥Îãπ Í∏Ä)Ïùò userÏùò IDÎ•º ÎπÑÍµêÌñàÏùÑ Îïå Í∞ôÏúºÎ©¥ ÏàòÏ†ï, ÏÇ≠Ï†ú Î≤ÑÌäºÏù¥ Î≥¥Ïù¥Í≤å ÌïúÎã§.
              IDÎäî DBÏóê Ï†ÄÏû•ÎêòÏñ¥ ÏûàÎäî Ïú†Ï†ÄÏùò Í≥†Ïú† Î≤àÌò∏Ïù¥Îã§.
             */
            id === board.userId &&
            <div className="edit-delete-button">
              <Button
                variant="outlined" color="error" endIcon={<DeleteForeverOutlinedIcon/>}
                className="delete-button"
                onClick={() => {
                  setShow(true)
                }}
              >
                ÏÇ≠Ï†ú
              </Button>
              <Button
                variant="outlined" endIcon={<BuildOutlinedIcon/>}
                onClick={() => {
                  navigate(`/marketchange/${boardId}`)
                }}
              >
                ÏàòÏ†ï
              </Button>
            </div>
          }

          <div className="w-full h-full flex justify-center items-center">
          {board && (

            <div className="dataform">
                Ï†úÎ™© <input value={board.title}/><br/>
                ÎÇ¥Ïö© <input value={board.content}/><br/>
                Ïù¥ÎØ∏ÏßÄ <img src={board.boardFilesId} alt={board.fileName} width="20%" height="200px"/><br/>
                Îì±Î°ùÏûê <input value={board.nickName}/><br/>
                Îì±Î°ùÏùº <input value={board.createDate}/><br/>
            </div>

            )
          }
          </div>

        </div>
    )}
      {/*modal*/}
      <Dialog open={show}>
        <DialogContent style={{position: "relative"}}>
          <IconButton
            style={{position: "absolute", top: "0", right: "0"}}
            onClick={() => setShow(false)}
          >
            <DisabledByDefaultOutlinedIcon/>
          </IconButton>
          <div className="modal">
            <div className="modal-title"> Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå ?</div>
            <div className="modal-button">
              <Button
                variant="outlined"
                color="error"
                onClick={async () => {
                  setShow(false);
                  // Î™®Îã¨Ïùò Ïòà Î≤ÑÌäº ÌÅ¥Î¶≠Ïãú Í≤åÏãúÎ¨º ÏÇ≠Ï†ú
                  Delete(board.boardId)
                  alert("Í≤åÏãúÎ¨ºÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§üòé");
                  window.location.href = "/market";
                }}
              >
                Ïòà
              </Button>
              <Button
                variant="outlined"
                color="primary"
                onClick={() => {
                  setShow(false)
                }}
              >
                ÏïÑÎãàÏò§
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </React.Fragment>
  );
}
export default MarketDetail;
