import {useParams} from "react-router-dom";
import {useCallback, useEffect, useState} from "react";
import {Button} from "@mui/material";
import axios from "axios";


const MarketChange = () => {
  const {boardsId} = useParams();
  // Í≤åÏãúÌåê Ï†úÎ™©, ÎÇ¥Ïö©, ÏÇ¨ÏßÑ
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState({
    image_file: "",
    preview_URL: `/api/v1/boards/${boardsId}/detail/images`});

    let inputRef;

    const saveImage = (e) => {
      e.preventDefault();
      const fileReader = new FileReader();
      if (e.target.files[0]) {
        fileReader.readAsDataURL(e.target.files[0]);
      }
      fileReader.onload = () => {
        setImage({
          image_file: e.target.files[0],
          preview_URL: fileReader.result,
        })
      };
    };

  useEffect(() => {
    const getBoard = async () => {
      axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
      const {data} = await axios.get(`/api/v1/boards/${boardsId}/detail`);
      return data;
    }
    getBoard().then((result) => {
      console.log(result)
      setTitle(result.title);
      setContent(result.content);
      // Ïù¥ÎØ∏ÏßÄÎäî ÌååÏùºÏùÑ Î∂àÎü¨Ïò¨ ÌïÑÏöîÍ∞Ä ÏóÜÏù¥ ÎØ∏Î¶¨Î≥¥Í∏∞ urlÎßå Í∞ÄÏ†∏Ïò®Îã§.
      // Ïù¥ÎØ∏ÏßÄÎ•º ÏÑ†ÌÉùÌïòÏßÄ ÏïäÍ≥† Ïò¨Î¶¨Î©¥ dbÏóê Ï†ÄÏû•ÎêòÏñ¥ ÏûàÎäî Ïù¥ÎØ∏ÏßÄÎ•º Í∑∏ÎåÄÎ°ú ÏÇ¨Ïö©!
      setImage({...image, preview_URL: `/api/v1/boards/${boardsId}/detail/images`})
      console.log(result.img)
      console.log(image)
    });
  }, [])


  const canSubmit = useCallback(() => {
    return content !== "" && title !== "";
  }, [image, title, content]);

  const handleSubmit = useCallback(async () => {
    try {

      const formData = new FormData();

      const data = {
        title: title,
        content: content
      }
      const json = JSON.stringify(data)
      const blob = new Blob([json], { type: "application/json" });
      formData.append("boardsUpdateRequest", blob);

      formData.append("files", image.image_file);

      console.log(...formData)

      axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
      await axios.put(`/api/v1/boards/${boardsId}`, formData);
      window.alert("ÏàòÏ†ïÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.");

      window.location.href = `/marketdetail/${boardsId}`;
    } catch (e) {


    }

  }, [canSubmit]);

  return (
    <div className="addBoard-wrapper">
      <div className="addBoard-header">
        Í≤åÏãúÎ¨º ÏàòÏ†ïÌïòÍ∏∞ üñäÔ∏è
      </div><br/>

      <div className="addBoard-body">
        <div className="text">
          Ï†ú Î™© <input
            onChange={(e) => {setTitle(e.target.value);}}
            className="title"
            value={title}
          /><br/>
          ÎÇ¥ Ïö© <input
            onChange={(e) => {setContent(e.target.value);}}
            className="content"
            value={content}
          />
        </div>

          <input
          type="file"
          accept="image/*"
          onChange={saveImage}
          ref={(refParam) => (inputRef = refParam)}
          style={{ display: "none" }}
        />
        <div className="img-wrapper">
          <img src={image.preview_URL} />
        </div>
        <div className="upload-button">
          <Button
            variant="outlined"
            color="primary"
            onClick={() => inputRef.click()}
          >
            Ïù¥ÎØ∏ÏßÄ ÏÑ†ÌÉù
          </Button>
        </div>
      </div>

      <div className="submitButton">
        {canSubmit() ? (
          <Button
            onClick={handleSubmit}
            className="success-button"
            variant="outlined"
          >
            ÏàòÏ†ïÌïòÍ∏∞
          </Button>
        ) : (
          <Button
            className="disable-button"
            variant="outlined"
            size="large"
          >
            Ï†úÎ™©Í≥º ÎÇ¥Ïö©ÏùÑ Î™®Îëê ÏûÖÎ†•ÌïòÏÑ∏Ïöîüò≠
          </Button>
        )}
      </div>
    </div>



  );
}

export default MarketChange
