import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { Button } from "react-bootstrap";
import ImageUploader from "./ImageUploader";
import TextArea from "./TextArea";
import { Toast } from "bootstrap";

const MarketChange = () => {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [img, setImg] = useState({
      image_file: "",
      preview_URL: "image/logo192.png"
    });

    const {boardsId} = useParams();

    useEffect(() => {
      const getBoard = async () => {
        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`
        const {data} = await axios.get(`/api/v1/boards/${boardsId}/detail`)
        return data
      }
      getBoard()
        .then((result)  => { 
          setTitle(result.title);
          setContent(result.content);
          setImg({preview_URL: `/api/v1/boards/${boardsId}/detail`});
          console.log(result.img)
        })

      }, [])

      const canSubmit = useCallback(() => {
        return title !== "" && content !== "";
      }, [img, title, content]);

      const handleSubmit = useCallback(async () => {
        try {
          const formData = new FormData();
          formData.append("title", title)
          formData.append("content", content)
          formData.append("file", img.image_file)
          axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
          await axios.put(`/api/v1/boards/${boardsId}`, formData)
          window.alert("ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.")
          window.location.href = "/market"
        } catch (e) {

        }
      }, [canSubmit])


      return (
        <div className="addBoard-wrapper">
          <div className="addBoard-header">
            Í≤åÏãúÎ¨º ÏàòÏ†ïÌïòÍ∏∞ üñäÔ∏è
          </div>
          <div className="submitButton">
            {canSubmit() ? (
              <Button
                onClick={handleSubmit}
                className="success-button"
                variant="outlined"
              >
                ÏàòÏ†ïÌïòÍ∏∞üòÉ
              </Button>
            ) : (
              <Button
                className="disable-button"
                variant="outlined"
                size="large"
              >
                Ï†úÎ™©Í≥º ÎÇ¥Ïö©ÏùÑ Î™®Îëê ÏûÖÎ†•ÌïòÏÑ∏Ïöîüò≠
              </Button>
            )}
          </div>
          <div className="addBoard-body">
            <ImageUploader setImage={setImg} preview_URL={img.preview_URL}/>
            <TextArea setTitle={setTitle} setContent={setContent} title={title} content={content}/>
          </div>
        </div>
      );
    }

export default MarketChange;
