import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { Button } from "react-bootstrap";

function MarketChange() {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [img, setImg] = useState("");
    const [look, setLook] = useState()

    const {boardsId} = useParams();

    // const One = () => {
    //   axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
    //     axios.get(`/api/v1/boards/${boardsId}/detail`)

    //     .then((response)  => { 
    //       setTitle(response.data.title);
    //       setContent(response.data.content);
    //       setImg(response.data.img);

    //     })
    // }

    useEffect(() => {
       const getBoard = async () => {
        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        axios.get(`/api/v1/boards/${boardsId}/detail`)
      }
        getBoard().then((request)  => { 
          setTitle(request.data.title);
          setContent(request.data.content);
          setImg(request.data.img);
          console.log(request.data.img)
        })

      }, [])

    // useEffect(One, [img])

      const data = {
        title: title,
        content: content,
        img: img
    }

      function Update(e) {
        e.preventDefault()

        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        axios.put(`/api/v1/boards/${boardsId}`, data)
        .then(
            window.location.href = "/market",
            alert('ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.')
        )
      }

      // const FileDelete = () => {
      //   response.data.img = ""
      //   console.log(data.img)
      // }


return (
    <>
    <h1>ÏàòÏ†ïÌïòÍ∏∞</h1>
    <form>
    Ï†ú Î™© <input 
        value={title}
        onChange={(e)=> {setTitle(e.target.value)}}
        type="text"
    /><br/>
    ÎÇ¥ Ïö© <input 
        value={content}
        onChange={(e)=> {setContent(e.target.value)}}
        type="text"
    /><br/>
    Ïù¥ÎØ∏ÏßÄ <img 
        src={img}
        alt=""
        width="20%" height="200px"
        // placeholder="Ïù¥ÎØ∏ÏßÄÎ•º Îì±Î°ùÌïòÏÑ∏Ïöî"
        // onChange={(e)=> {setImg(e.target.value)}}
    />
    {/* <button onClick={() => FileDelete()}>ÏÇ≠Ï†ú</button> */}

    <input 
          type="file"
          multiple="multiple"
          onChange={(e) => {setImg(e.target.value)}}
        /><br/>
  <Button href={`/marketdetail/${boardsId}`} variant="outline-secondary">&nbsp; Ï∑®ÏÜå &nbsp;</Button>
  <Button onClick={Update} variant="outline-success">&nbsp; ÏàòÏ†ï &nbsp;</Button>

    </form>
    </>
    )
}

export default MarketChange;
