import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import ImageUploader from "./ImageUploader";
import TextArea from "./TextArea";
import { Button } from "react-bootstrap";

function MarketChange() {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    const [img, setImg] = useState({
      image_file: "",
    });

    const {boardsId} = useParams();

    // const One = () => {
    //   axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
    //     axios.get(`/api/v1/boards/${boardsId}/detail`)

    //     .then((response)  => { 
    //       setTitle(response.data.title);
    //       setContent(response.data.content);
    //       setImg(response.data.img);

    //     })
    // }

    useEffect(() => {
       const getBoard = async () => {
        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        const {data} = await axios.get(`/api/v1/boards/${boardsId}/detail`)
        return data;
      }

        getBoard().then((request)  => { 
          setTitle(request.title);
          setContent(request.content);
          setImg(request.img);

        })

      }, [])


      const onFileDelete = useCallback(() => {
        return content !== "" && title !== "";
      }, [img, title, content])

    //   const data = {
    //     title: title,
    //     content: content,
    //     img: img
    // }

      const Update = useCallback(async () => {
        try {

        const formData = new FormData();
        formData.append("title", title);
        formData.append("content", content);
        formData.append("file", img.image_file);
        axios.defaults.headers.common['accessToken'] = `Bearer ${localStorage.getItem("access")}`;
        axios.put(`/api/v1/boards/${boardsId}`, formData)
            window.alert('ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§.')
            window.location.href = "/market";

        } catch(e) {

        }
      }, [onFileDelete]) 

      // const FileDelete = () => {
      //   response.data.img = ""
      //   console.log(data.img)
      // }


return (
    <>
    <h1>ÏàòÏ†ïÌïòÍ∏∞</h1>
    <div className="addBoard-body">
        <ImageUploader setImg={setImg} />
        <input
          onChange={(e) => {
            setTitle(e.target.value);
          }}
          className="title"
          placeholder="Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
          value={title}
        /><br/>
        <input
          onChange={(e) => {
            setContent(e.target.value);
          }}
          className="text"
          placeholder="ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
          value={content}
        /><br/>
      </div>
    <Button href={`/marketdetail/${boardsId}`} variant="outline-secondary">&nbsp; Ï∑®ÏÜå &nbsp;</Button>
    <Button onClick={Update} variant="outline-success">&nbsp; ÏàòÏ†ï &nbsp;</Button>

    </>
    )
}

export default MarketChange;
